import React, { useState } from 'react';
import './ReviewForm.css'; // Import the CSS file

const ReviewForm = ({ audiobookId, onReviewAdded }) => {
  const [name, setName] = useState('');
  const [reviewText, setReviewText] = useState('');
  const [rating, setRating] = useState(1);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Prepare review data to send
    const review = {
      name: name, // Correctly send 'name' as 'user'
      rating,
      comment: reviewText, // Correctly send 'reviewText' as 'comment'
    };

    console.log(review);

    try {
      const response = await fetch(`http://localhost:3000/api/audiobooks/${audiobookId}/reviews`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(review),
      });

      if (!response.ok) {
        throw new Error('Failed to submit review');
      }

      const newReview = await response.json();
      onReviewAdded(newReview); // Notify parent component of the new review
      setName(''); // Clear form fields
      setReviewText('');
      setRating(1);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <div>
          <label>
            Name:
            <input
              type="text"
              value= {name}
              onChange={(e) => setName(e.target.value)}
              required // Make the name field required
            />
          </label>
        </div>
        <label>
          Rating:
          <select value={rating} onChange={(e) => setRating(Number(e.target.value))}>
            {[1, 2, 3, 4, 5].map((rate) => (
              <option key={rate} value={rate}>
                {rate}
              </option>
            ))}
          </select>
        </label>
      </div>
      <div>
        <label>
          Review:
          <textarea value={reviewText} onChange={(e) => setReviewText(e.target.value)} />
        </label>
      </div>
      <button type="submit">Submit Review</button>
      {error && <p>Error: {error}</p>}
    </form>
  );
};

export default ReviewForm;
