// import { set } from 'mongoose';
import React, { useEffect, useState } from 'react';

import AudiobookDetails from './AudiobookCard';

const AudiobookList = () => {
  const [audiobooks, setAudiobooks] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [response, setResponse] = useState(null);

  useEffect(() => {
    const fetchAudiobooks = async () => {
      try {
        const response = await fetch('http://localhost:3000/api/audiobooks/');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        setResponse(response);
        const data = await response.json();
        console.log('Fetched data:', data);  // Debugging: Log fetched data
        setAudiobooks(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching audiobooks:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchAudiobooks();
  }, []);

  if (loading) {
    return (
    <div>

      Loading...
      </div>);
  }

  if (error) {
    return <div>
      <div> {audiobooks.length}</div>
      <div> {response}</div>
      Error: {error}
      </div>;
  }

  return (
    <div className="audiobook-list-container">
      {audiobooks.map((audiobook) => (
        <AudiobookDetails key={audiobook._id} audiobook={audiobook} />
      ))}
    </div>
  );
};

export default AudiobookList;
