import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const AudiobookList = () => {
  const [audiobooks, setAudiobooks] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  const [selectedGenre, setSelectedGenre] = useState('');
  const [selectedAuthor, setSelectedAuthor] = useState('');
  const [sortOption, setSortOption] = useState('');

  useEffect(() => {
    const fetchAudiobooks = async () => {
      try {
        const response = await fetch('http://localhost:3000/api/audiobooks');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setAudiobooks(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching audiobooks:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchAudiobooks();
  }, []);

  const handleGenreChange = (event) => {
    setSelectedGenre(event.target.value);
  };

  const handleAuthorChange = (event) => {
    setSelectedAuthor(event.target.value);
  };

  const handleSortChange = (event) => {
    setSortOption(event.target.value);
  };

  const getFilteredAudiobooks = () => {
    return audiobooks.filter((audiobook) => {
      return (
        (selectedGenre === '' || audiobook.genre === selectedGenre) &&
        (selectedAuthor === '' || audiobook.author === selectedAuthor)
      );
    });
  };

  const getSortedAudiobooks = (filteredAudiobooks) => {
    return filteredAudiobooks.sort((a, b) => {
      if (sortOption === 'rating') {
        return b.rating - a.rating;
      } else {
        return 0;
      }
    });
  };

  const filteredAudiobooks = getFilteredAudiobooks();
  const sortedAudiobooks = getSortedAudiobooks(filteredAudiobooks);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  const uniqueGenres = [...new Set(audiobooks.map(audiobook => audiobook.genre))];
  const uniqueAuthors = [...new Set(audiobooks.map(audiobook => audiobook.author))];

  return (
    <div className="audiobook-list-container">
      <div className="filters">
        <select value={selectedGenre} onChange={handleGenreChange}>
          <option value="">All Genres</option>
          {uniqueGenres.map((genre) => (
            <option key={genre} value={genre}>{genre}</option>
          ))}
        </select>

        <select value={selectedAuthor} onChange={handleAuthorChange}>
          <option value="">All Authors</option>
          {uniqueAuthors.map((author) => (
            <option key={author} value={author}>{author}</option>
          ))}
        </select>

        <select value={sortOption} onChange={handleSortChange}>
          <option value="">Sort By</option>
          <option value="rating">Rating</option>
        </select>
      </div>

      {sortedAudiobooks.length > 0 ? (
        <div className="audiobook-list">
          {sortedAudiobooks.map((audiobook) => (
            <div key={audiobook._id} className="audiobook-card">
              <img src={audiobook.coverImage} alt={audiobook.title} className="audiobook-cover" />
              <h2>{audiobook.title}</h2>
              <h3>by {audiobook.author}</h3>
              <p><strong>Genre:</strong> {audiobook.genre}</p>
              <p><strong>Description:</strong> {audiobook.description}</p>
              <p><strong>Rating:</strong> {audiobook.rating}</p>
              <Link to={`/audiobooks/${audiobook._id}`}>View details</Link>
            </div>
          ))}
        </div>
      ) : (
        <div>No audiobooks found</div>
      )}
    </div>
  );
};

export default AudiobookList;
