// src/components/AudiobookDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import ReviewForm from './ReviewForm';
import ReviewList from './ReviewList';

const AudiobookDetails = () => {
  const { id } = useParams();
  const [audiobook, setAudiobook] = useState(null);

  useEffect(() => {
    const fetchAudiobook = async () => {
      const result = await axios.get(`http://localhost:5000/api/audiobooks/${id}`);
      setAudiobook(result.data);
    };
    fetchAudiobook();
  }, [id]);

  const addReview = async (review) => {
    try {
      await axios.post(`http://localhost:5000/api/audiobooks/${id}/reviews`, review);
      // Refresh audiobook details after review submission
      const updatedAudiobook = await axios.get(`http://localhost:5000/api/audiobooks/${id}`);
      setAudiobook(updatedAudiobook.data);
    } catch (error) {
      console.error('Error adding review:', error);
    }
  };

  if (!audiobook) {
    return <div>Loading...</div>;
  }

  return (
    <div className="audiobook-details">
      <img src={audiobook.coverImage} alt={audiobook.title} className="audiobook-cover" />
      <h2>{audiobook.title}</h2>
      <h3>{audiobook.author}</h3>
      <p><strong>Genre:</strong> {audiobook.genre}</p>
      <p><strong>Description:</strong> {audiobook.description}</p>
      <p><strong>Rating:</strong> {audiobook.rating}</p>
      <h4>Reviews</h4>
      <ReviewList reviews={audiobook.reviews} />
      {/* <h4>Add a Review</h4>
      <ReviewForm onAddReview={addReview} /> */}
    </div>
  );
};

export default AudiobookDetails;
