import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import ReviewForm from './ReviewForm';
import ReviewList from './ReviewList';

const AudiobookDetails = () => {
  const { id } = useParams();
  const [audiobook, setAudiobook] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3000/api/audiobooks/${id}`)
      .then(response => response.json())
      .then(data => setAudiobook(data))
      .catch(error => console.error('Error fetching audiobook details:', error));
  }, [id]);

  const addReview = async (review) => {
    const response = await fetch('http://localhost:3000/api/reviews', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(review),
    });

    if (response.ok) {
      const newReview = await response.json();
      console.log('Review added:', newReview);
    } else {
      console.error('Error adding review:', response.status);
    }
  };

  async function fetchReviews() {
    const response = await fetch(`http://localhost:3000/api/audiobooks/${id}/reviews`);
    if (response.ok) {
      const reviews = await response.json();
      console.log('Reviews:', reviews);
    } else {
      console.error('Error fetching reviews:', response.status);
    }
  }





  if (!audiobook) {
    return <div>Loading...</div>;
  }



  return (
    <div className="audiobook-details">
      <img  alt={`${audiobook.title} cover`} />
      <h2>{audiobook.title}</h2>
      <p>Author: {audiobook.author}</p>
      <p>Genre: {audiobook.genre}</p>
      <p>Description: {audiobook.description}</p>
      <p>Rating: {audiobook.rating}</p>
      <ReviewForm audiobookId={id} addReview={addReview} />

    </div>
  );
};

export default AudiobookDetails;
