import React, { useEffect, useState } from 'react';
import AudiobookCard from './AudiobookCard';

const AudiobookList = () => {
  const [audiobooks, setAudiobooks] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAudiobooks = async () => {
      try {
        const response = await fetch('http://localhost:3000/api/audiobooks');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        console.log('Fetched data:', data);  // Debugging: Log fetched data
        setAudiobooks(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching audiobooks:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchAudiobooks();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div className="audiobook-list-container">
      <div className="audiobook-list">
        {audiobooks.length > 0 ? (
          audiobooks.map((audiobook) => (
            <AudiobookCard key={audiobook._id} audiobook={audiobook} />
          ))
        ) : (
          <div>No audiobooks found</div>
        )}
      </div>
    </div>
  );
};

export default AudiobookList;
