import React, { useEffect, useState } from 'react';
import AudiobookCard from './AudiobookCard';
import FilterBar from './FilterBar';

const AudiobookList = () => {
  const [audiobooks, setAudiobooks] = useState([]);
  const [filteredAudiobooks, setFilteredAudiobooks] = useState([]);
  const [filters, setFilters] = useState({ author: '', genre: '', rating: '' });

  useEffect(() => {
    fetch('http://localhost:5000/api/audiobooks')
      .then(response => response.json())
      .then(data => {
        setAudiobooks(data);
        setFilteredAudiobooks(data);
      })
      .catch(error => console.error('Error fetching audiobooks:', error));
  }, []);

  useEffect(() => {
    let filtered = audiobooks;
    if (filters.author) {
      filtered = filtered.filter(audiobook => audiobook.author.toLowerCase().includes(filters.author.toLowerCase()));
    }
    if (filters.genre) {
      filtered = filtered.filter(audiobook => audiobook.genre === filters.genre);
    }
    if (filters.rating) {
      filtered = filtered.filter(audiobook => audiobook.rating >= parseInt(filters.rating));
    }
    setFilteredAudiobooks(filtered);
  }, [filters, audiobooks]);

  const handleFilterChange = (filter, value) => {
    setFilters(prevFilters => ({ ...prevFilters, [filter]: value }));
  };

  return (
    <div className="audiobook-list-container">
      <FilterBar onFilterChange={handleFilterChange} />
      <div className="audiobook-list">
        {filteredAudiobooks.map(audiobook => (
          <AudiobookCard key={audiobook.id} audiobook={audiobook} />
        ))}
      </div>
    </div>
  );
};

export default AudiobookList;
