import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import FilterBar from './FilterBar'; // Assuming FilterBar is in a separate file

const AudiobookList = () => {
  const [audiobooks, setAudiobooks] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({ genre: '', author: '', minRating: '' });

  useEffect(() => {
    const fetchAudiobooks = async () => {
      try {
        const queryParams = new URLSearchParams(filters).toString();
        const response = await fetch(`http://localhost:3000/api/audiobooks?${queryParams}`);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setAudiobooks(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching audiobooks:', error);
        setError(error.message);
        setLoading(false);
      }
    };

    fetchAudiobooks();
  }, [filters]);

  const handleFilterChange = (newFilters) => {
    setFilters(newFilters);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <FilterBar onFilterChange={handleFilterChange} />

      <div className="audiobook-list-container">
        {audiobooks.length > 0 ? (
          <div className="audiobook-list">
            {audiobooks.map((audiobook) => (
              <div key={audiobook._id} className="audiobook-card">
                <img src={audiobook.coverImage} alt={audiobook.title} className="audiobook-cover" />
                <h2>{audiobook.title}</h2>
                <h3>by {audiobook.author}</h3>
                <p><strong>Genre:</strong> {audiobook.genre}</p>
                <p><strong>Description:</strong> {audiobook.description}</p>
                <p><strong>Rating:</strong> {audiobook.rating}</p>
                <Link to={`/audiobooks/${audiobook._id}`}>View details</Link>
              </div>
            ))}
          </div>
        ) : (
          <div>No audiobooks found</div>
        )}
      </div>
    </div>
  );
};

export default AudiobookList;
